// Create a minimal valid MP4 file for testing
// This is a 1-second black screen video encoded as base64
const fs = require('fs');
const path = require('path');

// Minimal valid MP4 (ftyp + moov + mdat atoms) - ~500 bytes
// This is the smallest possible valid MP4 that video players can open
const minimalMp4Base64 = '';

const buffer = Buffer.from(minimalMp4Base64, 'base64');

const outputPath = path.join(__dirname, 'test-video.mp4');
fs.writeFileSync(outputPath, buffer);

console.log(`Created test video: ${outputPath} (${buffer.length} bytes)`);
